    createChunk(file, size = 2 * 2000 * 100) {
      const chunkList = [];
      let cur = 0;
      while (cur < file.size) {
        chunkList.push({
          file: file.slice(cur, cur + size)
        });
        cur += size;
      }
      return chunkList;
