import Vue from "vue";
import Vuex from "vuex";
Vue.use(Vuex);
const store = new Vuex.Store({
  state: {
    token: localStorage.getItem("token") ? localStorage.getItem("token") : "",
  },
  getters: {
    getToken(state) {
      return state.token;
    },
  },
  mutations: {},
});
export default store;
