export const mixins = {
  data() {
    return {
      res: 1,
    };
  },
  computed: {
    community_id() {
      if (this.$store.getters.getPostInfo.community_list != undefined) {
        return this.$store.getters.getPostInfo.community_list[0].community_id;
      } else {
        return "1";
      }
    },
  },
  methods: {
    //building
    //builduingType
    buildingType(item) {
      let res = {};
      switch (item) {
        case 1:
          res = { value: "住宅", color: "red" };
          break;
        case 2:
          res = { value: "车位", color: "blue" };
          break;
        case 3:
          res = { value: "仓房", color: "pink" };
          break;
        case 4:
          res = { value: "商户", color: "skyblue" };
          break;
        case 5:
          res = { value: "车库", color: "orange" };
          break;
      }
      return res;
    },
    //detail
    detailInfo(item) {
      let types = this.buildingType(item.type);
      let res =
        (item.area ? item.area : "") +
        " " +
        (item.building ? item.building : "") +
        " " +
        (item.unit ? item.unit : "") +
        " " +
        item.number +
       ( types.value?types.value:'')
      return res;
    },
	//全屏
	screenfullEnter(){
		const el=document.documentElement
		const eventEnter=el.requestFullscreen||el.webkitRequestFullScreen||el.mozRequestFullScreen||el.msRequestFullScreen
		if(typeof eventEnter!=='undefined' && eventEnter){
			eventEnter.call(el)
		}
	}
  },
};
