import Vue from "vue";
import Vuex from "vuex";
import { request } from "@/network/config";
Vue.use(Vuex);
const store = new Vuex.Store({
  state: {
    token: localStorage.getItem("token") ? localStorage.getItem("token") : "",
    userInfo: {},
    postInfo: {},
  },
  getters: {
    getToken(state) {
      return state.token;
    },
    getUserInfo(state) {
      return state.userInfo;
    },
    getPostInfo(state) {
      return state.postInfo;
    },
  },
  mutations: {
    setToken(state, val) {
      state.token = val;
      localStorage.setItem("token", JSON.stringify(val));
    },
    removeToken(state) {
      state.token = null;
      localStorage.removeItem("token");
    },
    setUserInfo(state, val) {
      state.userInfo = Object.assign(state.userInfo, val);
    },
    setPostInfo(state, val) {
      state.postInfo = Object.assign(state.postInfo, val);
    },
  },
  actions: {
    getUserInfo(context) {
      trequest({
        url: "/user/info",
        method: "get",
      }).then((res) => {
        if (res.data.code === 200) {
			context.commit("setUserInfo", res.data.data.userInfo);
          context.commit("setPostInfo", res.data.data.postInfo);
        }
      });
    },
  },
});
export default store;
