import Vue from "vue";
import Router from "vue-router";
Vue.use(Router);
const myroutes = [
  require("@/views/home/redirectRouter"),
  require("@/views/basic/basicRouter"),
  require('@/views/iot/iotRouter')
];
const router = new Router({
  routes: myroutes,
});
export default router;
