import "core-js/modules/es.array.push.js";
import breadCrumb from "components/breadCrumb";
import axios from "axios";
export default {
  name: "ListVue",
  components: {
    breadCrumb
  },
  data() {
    return {
      chunkList: [],
      pro: 0,
      files: null
    };
  },
  mounted() {},
  methods: {
    parseFile(e) {
      this.files = e.target.files[0]; //读取文件
      //创建切片
      console.log(this.files);
      this.chunkList = this.createChunk(this.files);
      console.log(this.chunkList);
      //上传切片
    },

    createChunk(file, size = 2 * 2000 * 100) {
      const chunkList = [];
      let cur = 0;
      while (cur < file.size) {
        chunkList.push({
          file: file.slice(cur, cur + size)
        });
        cur += size;
      }
      return chunkList;
    },
    axiosRequest({
      method = "post",
      url,
      data
    }) {
      return new Promise((resolve, reject) => {
        const config = {
          //设置请求头
          headers: {
            "Content-Type": "application/x-www-form-urlencoded"
          }
        }; //默认是post请求，可更改
        axios[method](url, data, config).then(res => {
          resolve(res);
        });
      });
    },
    //对每个切片进行数据处理
    async uploadFile(list) {
      const requestList = list.map(({
        file,
        fileName,
        index,
        chunkName
      }) => {
        const formData = new FormData();
        formData.append("file", file);
        formData.append("fileName", fileName);
        formData.append("index", index);
        formData.append("chunkName", chunkName);
        return {
          formData,
          index
        };
      }).map(({
        formData,
        index
      }) => {
        this.axiosRequest({
          method: "post",
          url: "http://localhost:400/upload",
          data: formData
        }).then(res => {
          console.log(res);
          //显示每个切片上传的精度
          this.pro = `${list[index].chunkName}---${res.data.message}`;
        });
      });
      console.log(requestList);
      await Promise.all(requestList);
      let size = this.files.size;
      let name = this.files.name;
      console.log(size, name);
      await this.mergeChunk(size, name);
    },
    upload() {
      const uploadList = this.chunkList.map(({
        file
      }, index) => ({
        file,
        size: file.size,
        percent: 0,
        chunkName: `${this.files.name}-${index}`,
        fileName: this.files.name,
        index
      }));
      this.uploadFile(uploadList);
    },
    async mergeChunk(size, fileName) {
      console.log(JSON.stringify({
        size,
        fileName
      }));
      await this.axiosRequest({
        method: "post",
        url: "http://localhost:400/merge",
        data: JSON.stringify({
          size,
          fileName
        })
      }).then(res => {
        console.log(res);
      });
    }
  }
};
