import axios from "axios";
import router from "../router/index";
const codeMessage = {
  200: "服务器成功返回请求的数据",
  201: "新建或修改数据成功",
  204: "删除数据成功",
  400: "发出的请求有错误，服务器没有进行新建或修改数据的操作",
  401: "用户没有访问权限（令牌、用户名、密码错误）",
};
//登录注册时候的axios封装
export function mylocalrequest(config) {
  const instance = axios.create({
    baseURL: "http://127.0.0.1:300/pc",
  });
  //响应拦截器
  instance.interceptors.response.use((res) => {
    console.log(res);
    if (res.data.code === 200) {
      console.log(res);
      localStorage.setItem("refreshToken", res.data.data.refreshToken);
      this.notifySuccess(res.msg);
    }
    return res.data;
  });
  return instance(config);
}

export function myrequest(config) {
  const instance = axios.create({
    baseURL: "http://127.0.0.1:300/pc",
  });

  const clearAuth = () => {
    console.log("身份过期，请重新登录");
    window.location.replace("/user/login");
    localStorage.clear();
  };
  instance.interceptors.request.use((config) => {
    let token = localStorage.getItem("token");
    if (token) {
      config.headers["Authorization"] = token;
    }
    return config;
  });
  instance.interceptors.response.use(
    (res) => {
		console.log(res);
		if(res.data.code===401){
			console.log(5);
		}
      return res.data;
    },
    (res) => {
		console.log(4444);
		console.log(res);
      if (res.msg === "invalid token") {
        //token无效
        clearAuth();
      }
      if (res.error === "jwt expired") {
        //token过期
        console.log(3);
      }

      return res;
    }
  );
  return instance(config);
}

export function request(config) {
  const instance = axios.create({
    baseURL: "/pc",
  });

  instance.interceptors.response.use(
    (res) => {
      if (Object.keys(codeMessage).includes(`${res.data.code}`)) {
        this.notifySuccess(codeMessage[res.data.code]);
      }
      return res;
    },
    (err) => {
      return err;
    }
  );
  return instance(config);
}
