import tableComon from 'components/tableComon.vue';
import breadCrumb from "@/components/breadCrumb.vue";
import { mixins } from '@/minxin';
export default {
  name: "totalMeeting",
  components: {
    breadCrumb,
    tableComon
  },
  mixins: [mixins],
  data() {
    return {
      chose: 'my',
      myListHead: [{
        prop: "meetID",
        label: '会议编号',
        render(h, {
          row
        }) {
          return h('span', row.id);
        }
      }, {
        prop: "meetTime",
        label: "会议时间",
        render(h, {
          row
        }) {
          return h('span', `${this.standDate(row.created_at, 'YYYY-MM-DD HH:mm:ss')}-${this.standDate(row.end_time, 'YYYY-MM-DD HH:mm:ss')}`);
        }
      }, {
        prop: "theme",
        label: '会议主题'
      }, {
        prop: "name",
        label: '会议名称',
        key: 'name'
      }, {
        prop: "local",
        label: '会议位置',
        key: 'local'
      }, {
        prop: "status",
        label: "会议状态",
        key: 'cancel',
        render(h, {
          row
        }) {
          return h('el-tag', () => {
            let res;
            if (row.cancel == 0) {
              res = '已结束';
            } else {
              res = '待开始';
            }
            return res;
          });
        }
      }, {
        prop: 'created_by',
        label: "预定时间",
        render(h, {
          row
        }) {
          return h('span', `${this.standDate(row.created_by, 'YYYY-MM-DD HH:mm:ss')}`);
        }
      }, {
        prop: "action",
        label: '操作',
        render(h, {
          row
        }) {
          return h('span', '查看');
        }
      }],
      ListData: []
    };
  },
  mounted() {
    console.log(this.community_id);
    this.getMyData();
  },
  methods: {
    getMyData() {
      let data = {
        community_id: this.community_id,
        page_num: 1,
        page_size: 20
      };
      this.$request({
        url: "/meeting/detail/my",
        method: "post",
        data: data
      }).then(res => {
        console.log(res);
        this.ListData = res.data.data.list;
      });
    }
  }
};
