import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)
const store=new Vuex.Store({
	state:{
		currentType:_this.$route.path,
	},
	mutations:{
		//实现根据heade部分，选择不一样的侧边导航栏
		changeCurrent(state,val){
		state.currentType=val
	}
}
})
export default store
