import axios from "axios";
export function request(config) {
  const instance = axios.create({
    baseURL: "/pc",
  });
  instance.interceptors.response.use(
    (res) => {
      console.log(res);
      switch (res.data.code) {
        case 200:
          this.notifySuccess("操作成功");
          break;
      }
      return res;
    },
    (err) => {
      console.log(err);
      return err;
    }
  );
  return instance(config);
}
